const flagsElement=document.getElementById("flags"),textsToChange=document.querySelectorAll("[data-section]"),detectBrowserLanguage=()=>{let e=navigator.language||navigator.userLanguage;return e.toLowerCase()},changeLanguage=async e=>{let a=await fetch(`./javascript/translations/${e}.json`),t=await a.json();for(let n of textsToChange){let g=n.dataset.section,s=n.dataset.value;n.innerHTML=t[g][s]}};document.addEventListener("DOMContentLoaded",()=>{let e=detectBrowserLanguage(),a=["en","es"],t=a.includes(e)?e:"en";changeLanguage(t)}),flagsElement.addEventListener("click",e=>{changeLanguage(e.target.dataset.language)});