const showMenu=(e,n)=>{let t=document.getElementById(e),l=document.getElementById(n);t&&l&&t.addEventListener("click",()=>{l.classList.toggle("show-menu")})};showMenu("nav-toggle","nav-menu");const navLink=document.querySelectorAll(".nav__link");function linkAction(){let e=document.getElementById("nav-menu");e.classList.remove("show-menu")}navLink.forEach(e=>e.addEventListener("click",linkAction));